import React, { useState } from "react";
import styled from 'styled-components';
import TextField from '@material-ui/core/TextField';
import { makeStyles } from '@material-ui/core/styles';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';

import Button from '@material-ui/core/Button';

import {Inputs} from './StyledComponents.js';

const axios = require('axios');


const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

  const currencies = [
	{
	  value: 'USD',
	  label: '$',
	},
	{
	  value: 'EUR',
	  label: '€',
	},
	{
	  value: 'BTC',
	  label: '฿',
	},
	{
	  value: 'JPY',
	  label: '¥',
	},
  ];
function App(props) {

	const [isValidStockTicker, setIsValidStockTicker] = useState(true);
	const [isValidETFTicker, setIsValidETFTicker] = useState(true);

	async function sendGetRequest(etfValues, etfShares, stockValues, stockShares){
		let stocks = {};
		let etfs = [];
		let individualStocks=[];
		let totalOverallValue=0;
		for (let i=0; i<etfValues.length;i++){
			etfs.push({"etf": etfValues[i]["ticker"], "value": etfValues[i]["value"]});
			totalOverallValue+=etfValues[i]["value"];
		}
		for (let i=0; i<etfShares.length;i++){
			await axios.get(`https://financialmodelingprep.com/api/v3/quote-short/${etfShares[i]["ticker"]}?apikey=1a4f4c0ce5cd348fb5a01d33d0e1195a`)
			.then((response) => {
				console.log("H");
				etfs.push({"etf": etfShares[i]["ticker"], "value": etfShares[i]["shares"]*response.data[0]["price"]});
				totalOverallValue+=etfShares[i]["shares"]*response.data[0]["price"];
			})
			.catch(function (error){
				setIsValidETFTicker(false);
			});;
		}

		//now we have [{etf:SPY, value: 200}, {etf:SPY, value: 300}]
		for (let i=0;i<stockValues.length;i++){
			individualStocks.push({"stock": stockValues[i]["ticker"], "value":stockValues[i]["value"]});
			totalOverallValue+=stockValues[i]["value"];
		}


		for (let i=0;i<stockShares.length;i++){
			await axios.get(`https://financialmodelingprep.com/api/v3/quote-short/${stockShares[i]["ticker"]}?apikey=1a4f4c0ce5cd348fb5a01d33d0e1195a`)
				.then((response) => {

					individualStocks.push({"stock": stockShares[i]["ticker"], "value": stockShares[i]["shares"]*response.data[0]["price"]});
					totalOverallValue+=stockShares[i]["shares"]*response.data[0]["price"];
				})
				.catch(function (error){
					setIsValidStockTicker(false);
				});
		}
		console.log(totalOverallValue);
		//now we have [{stock:AAPL, value 50}, {stock:MSFT, value 50}]
		for(let i=0 ;i<etfs.length;i++){
			await axios.get(`https://fmpcloud.io/api/v3/etf-holder/${etfs[i]["etf"]}?apikey=863b5080b597b86a6bfab2088844d00a`)
			.then((response) => {
				for (let j=0;j<response.data.length;j++){
					stocks[response.data[j]["asset"]] = stocks[response.data[j]["asset"]] ? ((response.data[j]["weightPercentage"]/100)*etfs[i]["value"])/totalOverallValue + stocks[response.data[j]["asset"]] : ((response.data[j]["weightPercentage"]/100)*etfs[i]["value"])/totalOverallValue;
				}
			})
			.catch(function (error){
				setIsValidETFTicker(false);
			});;       
		}

		for (let i=0;i<individualStocks.length;i++){
			stocks[individualStocks[i]["stock"]] = stocks[individualStocks[i]["stock"]] ? individualStocks[i]["value"]/totalOverallValue + stocks[individualStocks[i]["stock"]]: individualStocks[i]["value"]/totalOverallValue;
		}

		return stocks;

	};



	const [formValues, setFormValues] = useState([{ "type": "ETF", "ETFStock" : "", "value":"Shares", "ValueShare":""}])

    // let handleChange = (i, e) => {
    //     let newFormValues = [...formValues];
    //     newFormValues[i][e.target.name] = e.target.value;
    //     setFormValues(newFormValues);
	//   }
	  const handleChange = name => event => {
		setValues({ ...values, [name]: event.target.value });
	  };

    let addFormFields = () => {
        setFormValues([...formValues, { "type": "ETF", "ETFStock" : "", "value":"Shares", "ValueShare":""}])
      }

    let removeFormFields = (i) => {
        let newFormValues = [...formValues];
        newFormValues.splice(i, 1);
        setFormValues(newFormValues)
	}
	function isNumeric(value) {
		return /^-?\d+$/.test(value);
	}
	const [triedSubmit, setTriedSubmit] = useState(false);
	const [empty, setEmpty] = useState(false);
	const [isNumber, setIsNumber] = useState(true);
    let handleSubmit = (event) => {
		setIsNumber(true);
		setIsValidStockTicker(true);
		setIsValidETFTicker(true);
		setEmpty(false);
		event.preventDefault();
		setTriedSubmit(true);
		let getOut=false;
		formValues.forEach(function (item, index) {
			console.log(typeof(item["ValueShare"]))
			if(item["ETFStock"]==="" || item["ValueShare"]===""){
				setEmpty(true);
				getOut=true;
				return;
			}
			if(isNumeric(item["ValueShare"])===false){
				console.log("e")
				setIsNumber(false);
				getOut=true;
				return;
			}

		});
		if(getOut===false){
			let etfShares=[];
			let etfValues=[];
			let stockShares=[];
			let stockValues=[];
			for (let i=0;i<formValues.length;i++){
				if(formValues[i]["type"]==="ETF" && formValues[i]["value"]==="Shares"){
					etfShares.push({"ticker":formValues[i]["ETFStock"], "shares":parseFloat(formValues[i]["ValueShare"])});
				}
				else if(formValues[i]["type"]==="ETF" && formValues[i]["value"]==="$ Value"){
					etfValues.push({"ticker":formValues[i]["ETFStock"], "value":parseFloat(formValues[i]["ValueShare"])});
				}
				else if(formValues[i]["type"]==="Stock" && formValues[i]["value"]==="Shares"){
					stockShares.push({"ticker":formValues[i]["ETFStock"], "shares":parseFloat(formValues[i]["ValueShare"])});
				}
				else if(formValues[i]["type"]==="Stock" && formValues[i]["value"]==="$ Value"){
					stockValues.push({"ticker":formValues[i]["ETFStock"], "value":parseFloat(formValues[i]["ValueShare"])});
				}
			}

			let percentages = sendGetRequest(etfValues, etfShares, stockValues, stockShares);

			percentages.then(function(result) {
				console.log(etfValues, etfShares, stockValues, stockShares);
				let entries = Object.entries(result);
				let sorted = entries.sort((a, b) => b[1] - a[1]);
				console.log(sorted);
			})
		}

    }

	const classes = useStyles();
	const [values, setValues] = React.useState({
		name: 'Cat in the Hat',
		age: '',
		multiline: 'Controlled',
		currency: 'EUR',
	});


	return (
		<Inputs>
		<form  className={classes.root} onSubmit={handleSubmit}>
          {formValues.map((element, index) => (
            <div className="form-inline" key={index}>


			<FormControl className={classes.formControl}>
			<InputLabel shrink id="demo-simple-select-placeholder-label-label">
				ETF or Stock
			</InputLabel>
				<Select name="type" onChange={e => handleChange(index, e)} labelId="demo-simple-select-placeholder-label-label"
          id="demo-simple-select-placeholder-label" displayEmpty className={classes.selectEmpty} value="ETF">
			  <MenuItem value="">
            <em>None</em>
          </MenuItem>
				<MenuItem value="ETF">ETF</MenuItem>
				<MenuItem value ="Stock">Stock</MenuItem>
				</Select>
			</FormControl>

              <TextField id="outlined-basic" variant ="outlined" type="text" name="ETFStock" value={element.ETFStock || ""} onChange={e => handleChange(index, e)} />

			  <FormControl className={classes.formControl}>
			<InputLabel shrink id="demo-simple-select-placeholder-label-label">
				Unit
			</InputLabel>
				<Select name="value" onChange={e => handleChange(index, e)} labelId="demo-simple-select-placeholder-label-label"
          id="demo-simple-select-placeholder-label" displayEmpty className={classes.selectEmpty} value="Shares">

				<MenuItem value="Shares">Shares</MenuItem>
				<MenuItem value ="$ Value">$ Value</MenuItem>
				</Select>
			</FormControl>

				<TextField id="outlined-basic" variant ="outlined" type="text" name="ValueShare" value={element.ValueShare || ""} onChange={e => handleChange(index, e)} />
              {
				index>0 ? 
				<Button variant="contained" type="button"  className="button remove" onClick={() => removeFormFields(index)}>Remove</Button>
                : null
              }
            </div>
          ))}
		  {triedSubmit && empty? <label>All inputs must be filled</label> :  triedSubmit && isNumber===false ? <label>Value input must be a valid number</label>: triedSubmit && isValidStockTicker===false? <label>Ensure that it is a valid stock ticker!</label>:triedSubmit && isValidETFTicker===false?<label>Ensure that it is a valid ETF!</label>:""}
          <div className="button-section">
			<Button variant="contained" color="primary" className="button add" type="button" onClick={() => addFormFields()}>
				Add
			</Button>
			<Button variant="contained" color="primary" className="button submit" type="submit">
				Submit
			</Button>

          </div>

      </form>
	  </Inputs>
  	);
}		

export default App;
