import TableHead from '@mui/material/TableHead';
import TablePagination from '@mui/material/TablePagination';
import TableRow from '@mui/material/TableRow';
import TableCustom from '/Table.js';
import {ButtonStyle,TopFlex, FlexItem,LastText, Title,TotalButton,ErrorLabel,SubTitle, PageColor} from './StyledComponents.js';

const axios = require('axios');

const darkTheme = createTheme({
	palette: {
	  mode: 'dark',
	},
});

const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));


function App(props) {
	const [rows, setRows] = React.useState()
	const [etfStockType, setEtfStockType] = React.useState('ETF');
