import React, { useState } from "react";
import styled from 'styled-components';
import { makeStyles } from '@material-ui/styles';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';
import classNames from 'classnames';


import {Inputs} from './StyledComponents.js';

const axios = require('axios');


const useStyles = makeStyles(theme => ({
	container: {
	  display: 'flex',
	  flexWrap: 'wrap',
	},
	textField: {
	  marginLeft: theme.spacing.unit,
	  marginRight: theme.spacing.unit,
	},
	dense: {
	  marginTop: 16,
	},
	menu: {
	  width: 200,
	},
  }));

  const currencies = [
	{
	  value: 'USD',
	  label: '$',
	},
	{
	  value: 'EUR',
	  label: '€',
	},
	{
	  value: 'BTC',
	  label: '฿',
	},
	{
	  value: 'JPY',
	  label: '¥',
	},
  ];
function App(props) {

	const [isValidStockTicker, setIsValidStockTicker] = useState(true);
	const [isValidETFTicker, setIsValidETFTicker] = useState(true);

	async function sendGetRequest(etfValues, etfShares, stockValues, stockShares){
		let stocks = {};
		let etfs = [];
		let individualStocks=[];
		let totalOverallValue=0;
		for (let i=0; i<etfValues.length;i++){
			etfs.push({"etf": etfValues[i]["ticker"], "value": etfValues[i]["value"]});
			totalOverallValue+=etfValues[i]["value"];
		}
		for (let i=0; i<etfShares.length;i++){
			await axios.get(`https://financialmodelingprep.com/api/v3/quote-short/${etfShares[i]["ticker"]}?apikey=1a4f4c0ce5cd348fb5a01d33d0e1195a`)
			.then((response) => {
				console.log("H");
				etfs.push({"etf": etfShares[i]["ticker"], "value": etfShares[i]["shares"]*response.data[0]["price"]});
				totalOverallValue+=etfShares[i]["shares"]*response.data[0]["price"];
			})
			.catch(function (error){
				setIsValidETFTicker(false);
			});;
		}

		//now we have [{etf:SPY, value: 200}, {etf:SPY, value: 300}]
		for (let i=0;i<stockValues.length;i++){
			individualStocks.push({"stock": stockValues[i]["ticker"], "value":stockValues[i]["value"]});
			totalOverallValue+=stockValues[i]["value"];
		}


		for (let i=0;i<stockShares.length;i++){
			await axios.get(`https://financialmodelingprep.com/api/v3/quote-short/${stockShares[i]["ticker"]}?apikey=1a4f4c0ce5cd348fb5a01d33d0e1195a`)
				.then((response) => {

					individualStocks.push({"stock": stockShares[i]["ticker"], "value": stockShares[i]["shares"]*response.data[0]["price"]});
					totalOverallValue+=stockShares[i]["shares"]*response.data[0]["price"];
				})
				.catch(function (error){
					setIsValidStockTicker(false);
				});
		}
		console.log(totalOverallValue);
		//now we have [{stock:AAPL, value 50}, {stock:MSFT, value 50}]
		for(let i=0 ;i<etfs.length;i++){
			await axios.get(`https://fmpcloud.io/api/v3/etf-holder/${etfs[i]["etf"]}?apikey=863b5080b597b86a6bfab2088844d00a`)
			.then((response) => {
				for (let j=0;j<response.data.length;j++){
					stocks[response.data[j]["asset"]] = stocks[response.data[j]["asset"]] ? ((response.data[j]["weightPercentage"]/100)*etfs[i]["value"])/totalOverallValue + stocks[response.data[j]["asset"]] : ((response.data[j]["weightPercentage"]/100)*etfs[i]["value"])/totalOverallValue;
				}
			})
			.catch(function (error){
				setIsValidETFTicker(false);
			});;       
		}

		for (let i=0;i<individualStocks.length;i++){
			stocks[individualStocks[i]["stock"]] = stocks[individualStocks[i]["stock"]] ? individualStocks[i]["value"]/totalOverallValue + stocks[individualStocks[i]["stock"]]: individualStocks[i]["value"]/totalOverallValue;
		}

		return stocks;

	};



	const [formValues, setFormValues] = useState([{ "type": "ETF", "ETFStock" : "", "value":"Shares", "ValueShare":""}])

    let handleChange = (i, e) => {
        let newFormValues = [...formValues];
        newFormValues[i][e.target.name] = e.target.value;
        setFormValues(newFormValues);
      }

    let addFormFields = () => {
        setFormValues([...formValues, { "type": "ETF", "ETFStock" : "", "value":"Shares", "ValueShare":""}])
      }

    let removeFormFields = (i) => {
        let newFormValues = [...formValues];
        newFormValues.splice(i, 1);
        setFormValues(newFormValues)
	}
	function isNumeric(value) {
		return /^-?\d+$/.test(value);
	}
	const [triedSubmit, setTriedSubmit] = useState(false);
	const [empty, setEmpty] = useState(false);
	const [isNumber, setIsNumber] = useState(true);
    let handleSubmit = (event) => {
		setIsNumber(true);
		setIsValidStockTicker(true);
		setIsValidETFTicker(true);
		setEmpty(false);
		event.preventDefault();
		setTriedSubmit(true);
		let getOut=false;
		formValues.forEach(function (item, index) {
			console.log(typeof(item["ValueShare"]))
			if(item["ETFStock"]==="" || item["ValueShare"]===""){
