		let stocks = {};
		let etfs = [];
		let individualStocks=[];
		let totalOverallValue=0;
		for (let i=0; i<etfValues.length;i++){
			etfs.push({"etf": etfValues[i]["ticker"], "value": etfValues[i]["value"]});
			totalOverallValue+=etfValues[i]["value"];
		}
