				console.log("H");
				etfs.push({"etf": etfShares[i]["ticker"], "value": etfShares[i]["shares"]*response.data[0]["price"]});
				totalOverallValue+=etfShares[i]["shares"]*response.data[0]["price"];
			});
		}

		//now we have [{etf:SPY, value: 200}, {etf:SPY, value: 300}]
