			individualStocks.push({"stock": stockValues[i]["ticker"], "value":stockValues[i]["value"]});
			totalOverallValue+=stockValues[i]["value"];
		}
		for (let i=0;i<stockShares.length;i++){
			await axios.get(`https://financialmodelingprep.com/api/v3/quote-short/${stockShares[i]["ticker"]}?apikey=1a4f4c0ce5cd348fb5a01d33d0e1195a`)
				.then((response) => {
					individualStocks.push({"stock": stockShares[i]["ticker"], "value": stockShares[i]["shares"]*response.data[0]["price"]});
					totalOverallValue+=stockShares[i]["shares"]*response.data[0]["price"];
				});
		}

		//now we have [{stock:AAPL, value 50}, {stock:MSFT, value 50}]
		for(let i=0 ;i<etfs.length;i++){
			await axios.get(`https://fmpcloud.io/api/v3/etf-holder/${etfs[i]["etf"]}?apikey=863b5080b597b86a6bfab2088844d00a`)
			.then((response) => {
				for (let j=0;j<response.data.length;j++){
					stocks[response.data[j]["asset"]] = stocks[response.data[j]["asset"]] ? ((response.data[j]["weightPercentage"]/100)*etfs[i]["value"])/totalOverallValue + stocks[response.data[j]["asset"]] : ((response.data[j]["weightPercentage"]/100)*etfs[i]["value"])/totalOverallValue;
				}
			});       
		}

		for (let i=0;i<individualStocks.length;i++){
			stocks[individualStocks[i]["stock"]] = stocks[individualStocks[i]["stock"]] ? individualStocks[i]["value"]/totalOverallValue + stocks[individualStocks[i]["stock"]]: individualStocks[i]["value"]/totalOverallValue;
		}
		return stocks;

	};

	let percentages = sendGetRequest([{"ticker": "SPY", "value":50}], [{"ticker": "SPY", "shares":1 }], [{"ticker": "AAPL", "value":50}], [{"ticker": "AAPL", "shares":1}]);

