	return (
		<div>
			<h1>Hey!</h1>
			{inputList}
			<button type="button" onClick={onAddClick}>Add EFT/Stock</button> <button type="button" onClick={onRemoveClick}>Remove EFT/Stock</button> 
			<br/>
			<button type="button">Calculate</button>
